var pcase = function(str) {
	str = str.toLowerCase();
	var characters = [' ', '-', '\'', '.'];
	characters.forEach(function(character) {
	if (str.indexOf(character) !== -1) {
		var newarr = [];
		var arr = str.split(character);
		arr.forEach(function(elem) {
			newarr.push(elem.charAt(0).toUpperCase()+elem.slice(1));
		});
		str = newarr.join(character);
	}
	});
	return str;
};

module.exports = pcase;