var pcase = require('../app.js');

var assert = require('assert');
describe('Test Suite', function() {
  describe('#pcase()', function () {
    it('spaces-lowercase', function () {
      assert.equal('Jim Test', pcase('jim test'));
    });
    it('spaces-uppercase', function () {
      assert.equal('Jim Test', pcase('JIM TEST'));
    });
    it('apostrophe-lowercase', function () {
      assert.equal("Conan O'Brien", pcase("conan o'brien"));
    });
    it('apostrophe-uppercase', function () {
      assert.equal("Conan O'Brien", pcase("CONAN O'BRIEN"));
    });
    it('random-mixture', function () {
      assert.equal("This-Is A Mix'Of'Tests For Mr.Smith And Mr.Jones", pcase("ThIs-iS a mix'oF'TESTS for MR.SMITH and mr.jones"));
    });
  });
});